export default function TodoReducer(state = [], action) {
	switch (action.type) {
		case 'ADD_TODO':
			return [
				...state,
				{
					id: Math.random() * (2 - 99999999) + 2,
					task: action.payload,
					completed: false
				}
			]
		case 'DELETE_TODO':
			return state.filter(el => {
				if (el.id !== action.payload) {
					return el
				} else {
					return;
				}
			})

		case 'TOGGLE_TODO':
			return state.map(el => {
				if (el.id === action.payload) {
					return {
						...el,
						completed: !el.completed
					}
				} else {
					return el
				}
			})

		default:
			return state
	}
}
